document.addEventListener("DOMContentLoaded",(function(e){function t(e){return elem=document.querySelector(e),elem}t("#burger-btn").addEventListener("click",()=>{[t("#burger-btn"),t("#burger")].forEach(e=>e.classList.toggle("active"))}),window.addEventListener("scroll",()=>{let e=[t("#container-menu"),t("#logo")],n=t("#description").clientHeight+t("#container-menu").clientHeight;window.pageYOffset>n?e.forEach(e=>e.classList.add("scroll")):e.forEach(e=>e.classList.remove("scroll"))})}));